import Link from 'next/link';

const NavItem = ({
    link = '/',
    heading,
    icon = 'seo',
    size = '60px',
    children,
}) => {
    const { theme } = useThemeUI();

    const primaryColor = theme.rawColors.primary;

    return (
        <Link href={link} scroll={false}>
            <a>
                <div
                    sx={{
                        bg: 'muted',
                        padding: '15px',
                        borderRadius: '10px',
                        transition: 'all 0.2s ease-out',
                        display: 'grid',
                        gap: '7px',
                        gridTemplateColumns: '1fr 4fr',
                        alignItems: 'center',
                        '&:hover': {
                            bg: lighten(primaryColor, 0.3),
                            '& .navItemIconBg': {
                                bg: primaryColor,
                                fill: 'alwaysLight',
                                stroke: 'alwaysLight',
                            },
                        },
                        '& .navItemIconBg': {
                            fill: 'primary',
                            stroke: 'primary',
                        },
                    }}
                >
                    <div
                        className="navItemIconBg"
                        sx={{
                            bg: lighten(primaryColor, 0.3),
                            maxWidth: size,
                            maxHeight: size,
                            borderRadius: '50%',
                        }}
                    >
                        <NavItemIcon icon={icon} />
                    </div>
                    <div sx={{ '*': { color: 'text' } }}>
                        <h3
                            sx={{
                                fontSize: '18px',
                                fontWeight: '600',
                                mb: ['5px', '5px', null, null],
                            }}
                        >
                            {heading}
                        </h3>
                        <p sx={{ fontSize: '16px', lineHeight: '1.2em' }}>
                            {children}
                        </p>
                    </div>
                </div>
            </a>
        </Link>
    );
};

